import React from 'react';


class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            reminders: [
                {
                    name: 'Buy some eggs',
                    timestamp: "2018-11-10T13:00:00.141Z"
                }
            ],
            newName: ''
        }
    }

    addReminder = (event) => {
        event.preventDefault()
        console.log('nappia painettu')
        console.log(event.target)
    }

    handleReminderChange = (event) => {
        console.log(event.target.value)
        this.setState({ newName: event.target.value })
    }

    render() {
        <div>
            debug: {this.state.newName}
        </div>

        return (
            <div>
                <h2>Reminders</h2>
                <form onSubmit={this.addReminder}>
                    <input value={this.state.newName}
                        onChange={this.handleReminderChange} />
                    <div>
                        <input />
                    </div>
                    <div>
                        <button type="submit">Lisää muistutus</button>
                    </div>
                </form>
                <h2>At time:</h2>
                <ul>
                    {this.state.reminders.map(reminder => <li key={reminder.timestamp}>{reminder.name}</li>)}
                </ul>
            </div>
        )
    }
}

export default App
